<span class="line"><span style="color:#F07178;">      __APP_ENV__</span><span style="color:#89DDFF;">:</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">JSON</span><span style="color:#89DDFF;">.</span><span style="color:#82AAFF;">stringify</span><span style="color:#F07178;">(</span><span style="color:#A6ACCD;">env</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">APP_ENV</span><span style="color:#F07178;">)</span><span style="color:#89DDFF;">,</span></span>
